document.addEventListener("keyup", (event) => {
    if (event.key === "s" || event.key === "S") {
        event.preventDefault();
        screencap();
    }
})

speed=0.375

let hydra, hydraCanvas;
hydraCanvas = document.createElement("canvas");
hydraCanvas.width = window.innerWidth
hydraCanvas.height = window.innerHeight
hydraCanvas.id = "hydraCanvas";
hydraCanvas.style.position = "absolute";
hydraCanvas.style.zIndex = 2;
hydraCanvas.style.top = 0;
hydraCanvas.style.left = 0;
hydraCanvas.getContext("webgl", { preserveDrawingBuffer: false });
hydra = new Hydra({
  canvas: hydraCanvas,
  detectAudio: false,
  enableStreamCapture: false,

});

document.querySelector(".placeholder").appendChild(hydraCanvas)

s0.initImage("./sacred.jpg")
s1.initImage("./abstract.jpg")
s2.initImage("./geometry.jpg")



src(s0)
.out(o0)

src(s1)
.out(o1)

src(s2)
.out(o2)

src(o3)
	.diff(src(o0)

	.add(src(o2)
		.invert(1)
		.scrollY(0.1,0.1)
		.modulate(osc(()=>30+mouse.x))
		.mult(osc(5,.02,2)
			.scrollX(() => mouse.x/250,0)
			.kaleid(()=>2.5+mouse.y/200)
			.rotate(0,2)
			.contrast(1.35)
			)
		)
	)

	.add(src(o2)
		.invert(1)
		.scrollY(0.1,0.1)
		.rotate(0,.05).contrast(1.2).brightness(0.12))

	.diff(src(o1)
		.invert(1)
		.scrollX(()=>mouse.x/500)
		.scrollY(()=>mouse.y/500)
		.scale(()=>.25+mouse.x/1000))
		.rotate(0,-0.1)

	.diff(src(o0)
		.scrollX(0,()=>0.002*Math.sin(Math.sin(.005*Math.cos(time*2000))))	
	)

.out(o3)
render(o3)
